type Main struct {
	router *gin.Engine
}

func (m *Main) initServer() error {
	gin.SetMode(gin.ReleaseMode)
	m.router = gin.New()

	file, err := os.Create("server.log")
	if err != nil {
		return err
	}
	gin.DefaultWriter = file

	m.router.Use(gin.LoggerWithWriter(gin.DefaultWriter))
	m.router.Use(gin.Recovery())

	return nil
}

func (m *Main) main() {
	if err := m.initServer(); err != nil {
		log.Fatalf("Failed to initialize server: %v", err)
	}

	authGroup := m.router.Group("/auth")
	authGroup.Use(m.jwtAuthMiddleware())
	{
		authGroup.GET("/protected", func(c *gin.Context) {
			c.JSON(200, gin.H{"message": "This is a protected route"})
		})
	}

	m.router.Run(":8080")
}

func (m *Main) jwtAuthMiddleware() gin.HandlerFunc {
	return func(c *gin.Context) {
		tokenString := c.GetHeader("Authorization")
		if tokenString == "" {
			c.JSON(401, gin.H{"error": "Request does not contain an access token"})
			c.Abort()
			return
		}

		token, err := jwt.Parse(tokenString, func(token *jwt.Token) (interface{}, error) {
			if _, ok := token.Method.(*jwt.SigningMethodHMAC); !ok {
				return nil, jwt.ErrSignatureInvalid
			}
			return []byte("secret"), nil
		})

		if err != nil || !token.Valid {
			c.JSON(401, gin.H{"error": "Invalid or expired access token"})
			c.Abort()
			return
		}

		c.Next()
	}
}

func main() {
	app := &Main{}
	app.main()
}

