		_, err = io.Copy(out, file)
		if err != nil {
			c.String(500, fmt.Sprintf("Error copying file: %s", err.Error()))
			return
		}

		// Get form values
		name := c.PostForm("name")
		email := c.PostForm("email")

		// Respond with a success message
		c.String(200, fmt.Sprintf("File %s uploaded successfully by %s (%s)", header.Filename, name, email))
	})

	return r
}

