	endpoint := os.Getenv("METADATA_ENDPOINT")
	if endpoint == "" {
		endpoint = "http://100.100.100.200"
	}
	r := fmt.Sprintf("%s/%s/%s/%s", endpoint, vpc.version, vpc.resourceType, vpc.resource)
	if vpc.subResource == "" {
		return r, nil
	}
	return fmt.Sprintf("%s/%s", r, vpc.subResource), nil
}

