	"encoding/json"
	"errors"
	"fmt"
	"io/ioutil"
	"net/http"
	"time"
)

type requestMock func(string) (string, error)

type MetaData struct {
	mock   requestMock
	client *http.Client
}

func NewMetaData(client *http.Client) *MetaData {
	if client == nil {
		client = &http.Client{Timeout: 10 * time.Second}
	}
	return &MetaData{client: client}
}

func (m *MetaData) New() *MetaDataRequest {
	return &MetaDataRequest{
		version:     "latest",
		resourceType: "meta-data",
		client:      m.client,
		sendRequest: m.mock,
	}
}

type MetaDataRequest struct {
	version      string
	resourceType string
	resource     string
	subResource  string
	client       *http.Client
	sendRequest  requestMock
}

type IMetaDataRequest interface {
	Version(string) IMetaDataRequest
	ResourceType(string) IMetaDataRequest
	Resource(string) IMetaDataRequest
	SubResource(string) IMetaDataRequest
	Url() (string, error)
	Do(interface{}) error
}

func (r *MetaDataRequest) Version(v string) IMetaDataRequest {
	r.version = v
	return r
}

func (r *MetaDataRequest) ResourceType(rt string) IMetaDataRequest {
	r.resourceType = rt
	return r
}

func (r *MetaDataRequest) Resource(res string) IMetaDataRequest {
	r.resource = res
	return r
}

func (r *MetaDataRequest) SubResource(sr string) IMetaDataRequest {
	r.subResource = sr
	return r
}

func (r *MetaDataRequest) Url() (string, error) {
	if r.resource == "" {
		return "", errors.New("resource path is required")
	}
	url := fmt.Sprintf("/%s/%s/%s", r.version, r.resourceType, r.resource)
	if r.subResource != "" {
		url += "/" + r.subResource
	}
	return url, nil
}

func (r *MetaDataRequest) Do(v interface{}) error {
	url, err := r.Url()
	if err != nil {
		return err
	}

	var body string
	if r.sendRequest != nil {
		body, err = r.sendRequest(url)
	} else {
		body, err = r.send()
	}
	if err != nil {
		return err
	}

	return r.Decode(body, v)
}

func (r *MetaDataRequest) send() (string, error) {
	req, err := http.NewRequest("GET", "https://metadata-service"+url, nil)
	if err != nil {
		return "", err
	}

	resp, err := r.client.Do(req)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	if resp.StatusCode < 200 || resp.StatusCode >= 300 {
		return "", fmt.Errorf("request failed with status %d", resp.StatusCode)
	}

	data, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	return string(data), nil
}

func (r *MetaDataRequest) Decode(body string, v interface{}) error {
	return json.NewDecoder(bytes.NewReader([]byte(body))).Decode(v)
}

