type Client struct {
	Protocol string
	Addr     string
	User     string
	Password string
	c        *http.Client
}

type ClientConfig struct {
	Protocol string
	Addr     string
	User     string
	Password string
}

type Response struct {
	Status     int
	Body       []byte
	ContentType string
	Error      error
}

func NewClient(conf *ClientConfig) *Client {
	tr := &http.Transport{
		TLSClientConfig: nil,
	}
	if conf.Protocol == "https" {
		tr.TLSClientConfig = &tls.Config{InsecureSkipVerify: true} // For demonstration purposes, consider using proper certificates
	}
	client := &http.Client{Transport: tr}
	return &Client{
		Protocol: conf.Protocol,
		Addr:     conf.Addr,
		User:     conf.User,
		Password: conf.Password,
		c:        client,
	}
}

func (cl *Client) DoRequest(method string, url string, body *bytes.Buffer) (*http.Response, error) {
	req, err := http.NewRequest(method, url, body)
	if err != nil {
		return nil, err
	}
	if cl.User != "" && cl.Password != "" {
		req.SetBasicAuth(cl.User, cl.Password)
	}
	return cl.c.Do(req)
}

func (cl *Client) Do(method string, url string, body map[string]interface{}) (*Response, error) {
	jsonBody, err := json.Marshal(body)
	if err != nil {
		return &Response{Error: err}, nil
	}
	resp, err := cl.DoRequest(method, fmt.Sprintf("%s://%s%s", cl.Protocol, cl.Addr, url), bytes.NewBuffer(jsonBody))
	if err != nil {
		return &Response{Error: err}, nil
	}
	defer resp.Body.Close()
	respBody, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return &Response{Error: err}, nil
	}
	return &Response{
		Status:     resp.StatusCode,
		Body:       respBody,
		ContentType: resp.Header.Get("Content-Type"),
	}, nil
}

