func Tcp(ctx context.Context, req *pb.TcpRequest, rsp *pb.TcpResponse) error {
	if req.Address == "" {
		rsp.Status = "Invalid address"
		return fmt.Errorf("invalid address")
	}

	conn, err := net.DialTimeout("tcp", req.Address, 5*time.Second)
	if err != nil {
		rsp.Status = err.Error()
		return nil
	}
	defer conn.Close()

	if ctx.Err() != nil {
		rsp.Status = ctx.Err().Error()
		return nil
	}

	if req.Data != "" {
		_, err := conn.Write([]byte(req.Data))
		if err != nil {
			rsp.Status = err.Error()
			return nil
		}
	}

	if ctx.Err() != nil {
		rsp.Status = ctx.Err().Error()
		return nil
	}

	conn.SetReadDeadline(time.Now().Add(5 * time.Second))
	data, err := ioutil.ReadAll(conn)
	if err != nil {
		rsp.Status = err.Error()
		return nil
	}

	rsp.Data = string(data)
	rsp.Status = "OK"
	return nil
}

