import (
	"errors"
	"net"
	"time"
)

type Config struct {
	KDCAddresses map[int]string
}

type Client struct {
	Config *Config
}

func (c *Client) sendKDCTCP(realm string, b []byte) ([]byte, error) {
	kdcs := c.Config.KDCAddresses
	return c.dialSendTCP(kdcs, b)
}

func (c *Client) dialSendTCP(kdcs map[int]string, b []byte) ([]byte, error) {
	var err error
	for _, kdc := range kdcs {
		conn, dialErr := net.DialTimeout("tcp", kdc, 5*time.Second)
		if dialErr != nil {
			err = errors.Join(err, dialErr)
			continue
		}
		tcpConn, ok := conn.(*net.TCPConn)
		if !ok {
			err = errors.Join(err, errors.New("failed to convert connection to TCP"))
			continue
		}
		resp, sendErr := c.sendTCP(tcpConn, b)
		if sendErr != nil {
			err = errors.Join(err, sendErr)
			continue
		}
		return resp, nil
	}
	return nil, err
}

func (c *Client) sendTCP(conn *net.TCPConn, b []byte) ([]byte, error) {
	defer conn.Close()
	_, writeErr := conn.Write(b)
	if writeErr != nil {
		return nil, writeErr
	}
	buf := make([]byte, 1024)
	n, readErr := conn.Read(buf)
	if readErr != nil {
		return nil, readErr
	}
	return buf[:n], nil
}

