func openTSDB(c *OpenTSDBConfig) error {
	conn, err := net.DialTCP("tcp", nil, c.Addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	var buf bytes.Buffer
	c.Registry.Each(func(name string, i interface{}) {
		switch metric := i.(type) {
		case metrics.Counter:
			buf.WriteString(fmt.Sprintf("%s%s value=%d %d\n", c.Prefix, name, metric.Count(), time.Now().Unix()))
		case metrics.Gauge:
			buf.WriteString(fmt.Sprintf("%s%s value=%d %d\n", c.Prefix, name, metric.Value(), time.Now().Unix()))
		case metrics.Histogram:
			histSnapshot := metric.Snapshot()
			buf.WriteString(fmt.Sprintf("%s%s.min value=%d %d\n", c.Prefix, name, histSnapshot.Min(), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.max value=%d %d\n", c.Prefix, name, histSnapshot.Max(), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.mean value=%.2f %d\n", c.Prefix, name, histSnapshot.Mean(), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.stddev value=%.2f %d\n", c.Prefix, name, histSnapshot.StdDev(), time.Now().Unix()))
			percentiles := []float64{50.0, 75.0, 95.0, 99.0}
			values := histSnapshot.Percentiles(percentiles)
			for i, percentile := range percentiles {
				buf.WriteString(fmt.Sprintf("%s%s.p%.2f value=%.2f %d\n", c.Prefix, name, percentile, values[i], time.Now().Unix()))
			}
		case metrics.Meter:
			meterSnapshot := metric.Snapshot()
			buf.WriteString(fmt.Sprintf("%s%s.count value=%d %d\n", c.Prefix, name, meterSnapshot.Count(), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.m1_rate value=%.2f %d\n", c.Prefix, name, meterSnapshot.Rate1(), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.m5_rate value=%.2f %d\n", c.Prefix, name, meterSnapshot.Rate5(), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.m15_rate value=%.2f %d\n", c.Prefix, name, meterSnapshot.Rate15(), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.mean_rate value=%.2f %d\n", c.Prefix, name, meterSnapshot.RateMean(), time.Now().Unix()))
		case metrics.Timer:
			timerSnapshot := metric.Snapshot()
			buf.WriteString(fmt.Sprintf("%s%s.min value=%d %d\n", c.Prefix, name, timerSnapshot.Min()/int64(c.DurationUnit), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.max value=%d %d\n", c.Prefix, name, timerSnapshot.Max()/int64(c.DurationUnit), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.mean value=%.2f %d\n", c.Prefix, name, timerSnapshot.Mean()/float64(c.DurationUnit), time.Now().Unix()))
			buf.WriteString(fmt.Sprintf("%s%s.stddev value=%.2f %d\n", c.Prefix, name, timerSnapshot.StdDev()/float64(c.DurationUnit), time.Now().Unix()))
			percentiles := []float64{50.0, 75.0, 95.0, 99.0}
			values := timerSnapshot.Percentiles(percentiles)
			for i, percentile := range percentiles {
				buf.WriteString(fmt.Sprintf("%s%s.p%.2f value=%.2f %d\n", c.Prefix, name, percentile, values[i]/float64(c.DurationUnit), time.Now().Unix()))
			}
		}
	})

	_, err = conn.Write(buf.Bytes())
	return err
}

