func (c *Client) Exchange(m *Msg, address string) (*Msg, time.Duration, error) {
	if c.SingleInflight {
		key := exchangeKey(m, address)
		v, err, _ := c.group.Do(key, func() (interface{}, error) {
			return c.exchange(m, address)
		})
		if err != nil {
			return nil, 0, err
		}
		msg, ok := v.(*Msg)
		if !ok {
			return nil, 0, ErrUnexpected
		}
		return msg, 0, nil
	}
	return c.exchange(m, address)
}

func (c *Client) exchange(m *Msg, a string) (*Msg, time.Duration, error) {
	start := time.Now()
	conn, err := c.Dial(a)
	if err != nil {
		return nil, 0, err
	}
	defer conn.Close()

	timeout := c.getTimeoutForRequest(c.Timeout)
	conn.SetDeadline(time.Now().Add(timeout))

	err = conn.WriteMsg(m)
	if err != nil {
		return nil, 0, err
	}

	rm, err := conn.ReadMsg()
	if err != nil {
		return nil, 0, err
	}

	return rm, time.Since(start), nil
}

func (c *Client) getTimeoutForRequest(timeout time.Duration) time.Duration {
	if timeout <= 0 {
		timeout = 2 * time.Second
	}
	return timeout
}

type Conn struct {
	net.Conn
	ReadTimeout  time.Duration
	WriteTimeout time.Duration
}

func (c *Conn) WriteMsg(m *Msg) error {
	if c.WriteTimeout > 0 {
		c.Conn.SetWriteDeadline(time.Now().Add(c.WriteTimeout))
	}
	_, err := m.PackBuffer(nil)
	if err != nil {
		return err
	}
	_, err = c.Write(m.Msg)
	return err
}

func (c *Conn) ReadMsg() (*Msg, error) {
	if c.ReadTimeout > 0 {
		c.Conn.SetReadDeadline(time.Now().Add(c.ReadTimeout))
	}
	buf := make([]byte, MaxMsgSize)
	n, err := c.Read(buf)
	if err != nil {
		return nil, err
	}
	msg := new(Msg)
	err = msg.Unpack(buf[:n])
	if err != nil {
		return nil, err
	}
	return msg, nil
}

