	listener, err := net.Listen("tcp", d.addr)
	if err != nil {
		return err
	}
	defer listener.Close()
	d.logger.Info("Listening on %s", d.addr)
	return d.Serve(listener)
}

func (d *GitDaemon) Serve(l net.Listener) error {
	var g errgroup.Group
	g.Go(func() error {
		<-d.ctx.Done()
		return l.Close()
	})
	g.Go(func() error {
		for {
			conn, err := l.Accept()
			if err != nil {
				if ne, ok := err.(net.Error); ok && ne.Temporary() {
					time.Sleep(d.cfg.Backoff)
					continue
				}
				return err
			}
			d.wg.Add(1)
			go func(conn net.Conn) {
				defer d.wg.Done()
				d.handleClient(conn)
			}(conn)
		}
	})
	if err := g.Wait(); err != nil && !errors.Is(err, net.ErrClosed) {
		return err
	}
	return nil
}

func (d *GitDaemon) handleClient(conn net.Conn) {
	defer conn.Close()

	sc := &serverConn{
		Conn:         conn,
		readTimeout:  d.cfg.ReadTimeout,
		writeTimeout: d.cfg.WriteTimeout,
		idleTimeout:  d.cfg.IdleTimeout,
	}

	if !d.conns.Add(sc) {
		d.logger.Warn("Max connections reached, rejecting connection from %s", sc.RemoteAddr())
		return
	}
	defer d.conns.Delete(sc)

	if err := sc.SetDeadline(time.Now().Add(d.cfg.IdleTimeout)); err != nil {
		d.logger.Error("Failed to set deadline for connection from %s: %v", sc.RemoteAddr(), err)
		return
	}

	service, repoPath, err := sc.readServicePacket()
	if err != nil {
		d.logger.Error("Failed to read service packet from %s: %v", sc.RemoteAddr(), err)
		return
	}

	if !d.be.IsValidRepo(repoPath) {
		d.logger.Warn("Invalid repository path requested by %s: %s", sc.RemoteAddr(), repoPath)
		sc.writeError("invalid repository path")
		return
	}

	if !d.be.HasAccess(repoPath, service) {
		d.logger.Warn("Access denied to %s for %s by %s", repoPath, service, sc.RemoteAddr())
		sc.writeError("access denied")
		return
	}

	switch service {
	case "git-upload-pack":
		d.uploadPack(sc, repoPath)
	case "git-upload-archive":
		d.uploadArchive(sc, repoPath)
	default:
		d.logger.Warn("Unsupported service requested by %s: %s", sc.RemoteAddr(), service)
		sc.writeError("unsupported service")
	}
}

type serverConn struct {
	net.Conn
	readTimeout  time.Duration
	writeTimeout time.Duration
	idleTimeout  time.Duration
	lastActivity time.Time
	mu           sync.Mutex
}

func (sc *serverConn) SetDeadline(t time.Time) error {
	sc.mu.Lock()
	defer sc.mu.Unlock()
	sc.lastActivity = t
	return sc.Conn.SetDeadline(t)
}

func (sc *serverConn) Read(b []byte) (n int, err error) {
	sc.mu.Lock()
	defer sc.mu.Unlock()
	err = sc.Conn.SetReadDeadline(time.Now().Add(sc.readTimeout))
	if err != nil {
		return
	}
	n, err = sc.Conn.Read(b)
	sc.lastActivity = time.Now()
	return
}

func (sc *serverConn) Write(b []byte) (n int, err error) {
	sc.mu.Lock()
	defer sc.mu.Unlock()
	err = sc.Conn.SetWriteDeadline(time.Now().Add(sc.writeTimeout))
	if err != nil {
		return
	}
	n, err = sc.Conn.Write(b)
	sc.lastActivity = time.Now()
	return
}

func (sc *serverConn) readServicePacket() (service, repoPath string, err error) {
	buf := make([]byte, 4096)
	n, err := sc.Read(buf)
	if err != nil {
		return
	}
	packet := buf[:n]
	if len(packet) < 4 || packet[0] != 0 || packet[1] != 0 || packet[2] != 0 {
		err = fmt.Errorf("invalid packet header")
		return
	}
	length := int(packet[3])
	if length > len(packet)-4 {
		err = fmt.Errorf("packet too short")
		return
	}
	payload := packet[4:length]
	parts := splitPktLine(payload)
	if len(parts) < 2 {
		err = fmt.Errorf("invalid service packet")
		return
	}
	service = parts[0]
	repoPath = parts[1]
	return
}

func (sc *serverConn) writeError(msg string) {
	sc.Write([]byte(fmt.Sprintf("ERR %s\n", msg)))
}

