func splitPktLine(payload []byte) []string {
	var parts []string
	for len(payload) > 0 {
		end := bytes.IndexByte(payload, 0)
		if end == -1 {
			parts = append(parts, string(payload))
			break
		}
		parts = append(parts, string(payload[:end]))
		payload = payload[end+1:]
	}
	return parts
}

func (c *connections) Add(conn *serverConn) bool {
	if c.Len() >= d.cfg.MaxConnections {
		return false
	}
	c.Store(conn, struct{}{})
	return true
}

