func (t *Trojan) Serve(c net.Conn) {
	defer c.Close()
	var conn net.Conn = c
	if t.withTLS {
		conn = tls.Server(c, t.tlsConfig)
	}

	buf := new(bytes.Buffer)
	io.CopyN(buf, conn, 2)
	cmd, target, err := t.readHeader(io.MultiReader(buf, conn))
	if err != nil {
		fmt.Println("Error reading header:", err)
		return
	}

	switch cmd {
	case socks.CmdConnect:
		t.handleTCP(conn, target, buf)
	case socks.CmdUDPAssociate:
		t.ServeUoT(conn, target)
	default:
