func (t *Trojan) readHeader(r io.Reader) (cmd socks.Cmd, target socks.Addr, err error) {
	br := bufio.NewReader(r)
	cmdByte, err := br.ReadByte()
	if err != nil {
		return
	}
	cmd = socks.Cmd(cmdByte)

	addrType, err := br.ReadByte()
	if err != nil {
		return
	}

	var host string
	switch addrType {
	case socks.AtypIPv4:
		var ip [4]byte
		if _, err = io.ReadFull(br, ip[:]); err != nil {
			return
		}
		host = net.IP(ip[:]).String()
	case socks.AtypDomainName:
		length, err := br.ReadByte()
		if err != nil {
			return
		}
		domain := make([]byte, length)
		if _, err = io.ReadFull(br, domain); err != nil {
			return
		}
		host = string(domain)
	case socks.AtypIPv6:
		var ip [16]byte
		if _, err = io.ReadFull(br, ip[:]); err != nil {
			return
		}
		host = net.IP(ip[:]).String()
	default:
		err = fmt.Errorf("unknown address type %d", addrType)
		return
	}

	portBytes, err := br.Peek(2)
	if err != nil {
		return
	}
