	}

	fallbackConn, err := net.Dial("tcp", fallback)
	if err != nil {
		fmt.Println("Fallback connection failed:", err)
		return
	}
	defer fallbackConn.Close()

	c.Write([]byte{0x05, 0x00})
	_, _ = io.Copy(fallbackConn, io.MultiReader(buf, c))
	_, _ = io.Copy(c, fallbackConn)
