func (t *Trojan) ServeUoT(c net.Conn, target socks.Addr) {
	session, err := kcp.NewConn(target.String(), nil, 10, 3)
	if err != nil {
		fmt.Println("Error creating KCP session:", err)
		return
	}
	defer session.Close()

	var key [32]byte
	copy(key[:], t.pass[:])
	aead, err := chacha20poly1305.New(key[:])
	if err != nil {
		fmt.Println("Error creating AEAD:", err)
		return
	}

	go func() {
		buffer := make([]byte, 4096)
		for {
			n, err := c.Read(buffer)
			if err != nil {
				break
			}
			nonce := make([]byte, aead.NonceSize())
			ciphertext := aead.Seal(nonce, nonce, buffer[:n], nil)
			_, _ = session.Write(ciphertext)
		}
	}()

	buffer := make([]byte, 4096)
	for {
		n, err := session.Read(buffer)
		if err != nil {
			break
		}
		nonceSize := aead.NonceSize()
		if n < nonceSize {
			continue
		}
		plaintext, err := aead.Open(nil, buffer[:nonceSize], buffer[nonceSize:n], nil)
		if err != nil {
			continue
		}
		_, _ = c.Write(plaintext)
	}
}

