type Cmd byte

const (
	CmdConnect      Cmd = 0x01
	CmdBind         Cmd = 0x02
	CmdUDPAssociate Cmd = 0x03
)

type Atyp byte

const (
	AtypIPv4   Atyp = 0x01
	AtypDomain Atyp = 0x03
	AtypIPv6   Atyp = 0x04
)

type Addr string

func ParseAddr(s string) Addr {
	var atyp Atyp
	host, port, err := net.SplitHostPort(s)
	if err != nil {
		return ""
	}
	ip := net.ParseIP(host)
	if ip == nil {
		atyp = AtypDomain
	} else if ip.To4() != nil {
		atyp = AtypIPv4
	} else {
		atyp = AtypIPv6
	}

	switch atyp {
	case AtypDomain:
		return Addr(string([]byte{byte(atyp), byte(len(host))}) + host + port)
	case AtypIPv4:
		return Addr(string([]byte{byte(atyp)}) + host + port)
	case AtypIPv6:
		return Addr(string([]byte{byte(atyp)}) + host + port)
	}
	return ""
}

