func (h *HTTP) ListenAndServe() {
	listener, err := net.Listen("tcp", h.addr)
	if err != nil {
		panic(err)
	}
	defer listener.Close()
	for {
		conn, err := listener.Accept()
		if err != nil {
			continue
		}
		go h.Serve(conn)
	}
}

func (h *HTTP) Serve(conn net.Conn) {
	defer conn.Close()
	conn.SetKeepAlive(true)
	conn.SetKeepAlivePeriod(3 * time.Minute)
	reader := bufio.NewReader(conn)
	for {
		req, err := http.ReadRequest(reader)
		if err != nil {
			return
		}
		conn2, err := h.proxy.Dial("tcp", req.Host)
		if err != nil {
			http.Error(conn, "Bad Gateway", http.StatusBadGateway)
			return
		}
		prxyConn := &proxy.Conn{Conn: conn2, Target: req.Host}
		h.servRequest(&request{req}, prxyConn)
	}
}

type request struct {
	*http.Request
}

func (h *HTTP) servRequest(req *request, prxyConn *proxy.Conn) {
	if h.pretend {
		http.NotFound(prxyConn.Conn, req.Request)
		return
	}
	if h.user != "" && h.password != "" {
		username, password, ok := req.BasicAuth()
		if !ok || username != h.user || password != h.password {
			w := http.NewResponseController(prxyConn.Conn)
			w.Header().Set("WWW-Authenticate", `Basic realm="Restricted"`)
			w.WriteHeader(http.StatusUnauthorized)
			return
		}
	}
	if req.Method == http.MethodConnect {
		h.servHTTPS(req, prxyConn.Conn)
	} else {
		h.servHTTP(req, prxyConn)
	}
}

func (h *HTTP) servHTTPS(req *request, conn net.Conn) {
	conn.Write([]byte("HTTP/1.1 200 Connection Established\r\n\r\n"))
	tlsConfig := &tls.Config{InsecureSkipVerify: true}
	tlsConn := tls.Server(conn, tlsConfig)
	tlsConn.Handshake()
	h.proxy.Dial("tcp", req.Host)
}

func (h *HTTP) servHTTP(req *request, prxyConn *proxy.Conn) {
	req.Header.Del("Proxy-Connection")
	req.Header.Add("Connection", "close")
	req.URL.Scheme = "http"
	req.URL.Host = req.Host
	resp, err := http.DefaultTransport.RoundTrip(req.Request)
	if err != nil {
		http.Error(prxyConn.Conn, "Bad Gateway", http.StatusBadGateway)
		return
	}
	defer resp.Body.Close()
	copyHeader(prxyConn.Conn.Header(), resp.Header)
	prxyConn.WriteHeader(resp.StatusCode)
	io.Copy(prxyConn.Conn, resp.Body)
	prxyConn.Conn.Close()
}

func copyHeader(dst, src http.Header) {
	for k, vv := range src {
		for _, v := range vv {
			dst.Add(k, v)
		}
	}
}

func main() {
	dialer, err := proxy.SOCKS5("tcp", "127.0.0.1:1080", nil, proxy.Direct)
	if err != nil {
		panic(err)
	}
	server := &HTTP{
		addr:     ":8080",
		user:     "user",
		password: "pass",
		pretend:  false,
		proxy:    dialer,
	}
	server.ListenAndServe()
}

