package influxdb

import (
	"context"
	"crypto/tls"
	"encoding/json"
	"fmt"
	"io/ioutil"
	"net/http"
	"net/url"
	"strings"
	"time"

	"golang.org/x/net/proxy"
)

const (
	defaultUserAgent = "influxdb-go-client"
	defaultPrecision = "ns"
)

type Config struct {
	URL        *url.URL
	UnixSocket string
	Username   string
	Password   string
	TLSConfig  *tls.Config
	Proxy      func(*http.Request) (*url.URL, error)
	Timeout    time.Duration
	UserAgent  string
	Precision  string
}

type Client struct {
	url        *url.URL
	unixSocket string
	username   string
	password   string
	httpClient *http.Client
	userAgent  string
	precision  string
}

func NewClient(config Config) (*Client, error) {
	if config.URL == nil && config.UnixSocket == "" {
		return nil, fmt.Errorf("either URL or UnixSocket must be set")
	}

	var transport http.RoundTripper
	if config.UnixSocket != "" {
		transport = &http.Transport{
			DialContext: func(_ context.Context, _, _ string) (net.Conn, error) {
				return net.Dial("unix", config.UnixSocket)
			},
		}
	} else if config.Proxy != nil {
		dialer, err := proxy.FromURL(config.Proxy(&http.Request{URL: config.URL}), proxy.Direct)
		if err != nil {
			return nil, err
		}
		transport = &http.Transport{
			DialContext:         dialer.Dial,
			TLSHandshakeTimeout: config.Timeout,
			TLSClientConfig:     config.TLSConfig,
		}
	} else {
		transport = &http.Transport{
			TLSHandshakeTimeout: config.Timeout,
			TLSClientConfig:     config.TLSConfig,
		}
	}

	client := &Client{
		url:        config.URL,
		unixSocket: config.UnixSocket,
		username:   config.Username,
		password:   config.Password,
		httpClient: &http.Client{
			Transport: transport,
			Timeout:   config.Timeout,
		},
		userAgent:  config.UserAgent,
		precision:  config.Precision,
	}

	if client.userAgent == "" {
		client.userAgent = defaultUserAgent
	}

	if client.precision == "" {
		client.precision = defaultPrecision
	}

	return client, nil
}

type Query struct {
	Command string
	Params  map[string]string
}

type Response struct {
	Results []Result `json:"results"`
	Error   string   `json:"error"`
}

type Result struct {
	Series []Series `json:"series"`
	Error  string   `json:"error"`
}

type Series struct {
	Name    string            `json:"name"`
	Columns []string          `json:"columns"`
	Values  [][]interface{} `json:"values"`
}

func (c *Client) QueryContext(ctx context.Context, q Query) (*Response, error) {
	queryValues := url.Values{}
	for k, v := range q.Params {
		queryValues.Add(k, v)
	}
	queryValues.Add("q", q.Command)
	queryValues.Add("epoch", c.precision)

	u := *c.url
	u.Path = "/query"
	u.RawQuery = queryValues.Encode()

	req, err := http.NewRequestWithContext(ctx, "GET", u.String(), nil)
	if err != nil {
		return nil, err
	}

	req.Header.Set("User-Agent", c.userAgent)
	if c.username != "" && c.password != "" {
		req.SetBasicAuth(c.username, c.password)
	}

	resp, err := c.httpClient.Do(req)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	if resp.StatusCode < 200 || resp.StatusCode >= 300 {
		return nil, fmt.Errorf("influxdb: %s", strings.TrimSpace(string(body)))
	}

	var response Response
	if err := json.Unmarshal(body, &response); err != nil {
		return nil, err
	}

	if response.Error != "" {
		return nil, fmt.Errorf("influxdb: %s", response.Error)
	}

	return &response, nil
}

type BatchPoints struct {
	Database        string
	RetentionPolicy string
	Points          []Point
}

type Point struct {
	Measurement string
	Tags        map[string]string
	Fields      map[string]interface{}
	Time        time.Time
}

func (c *Client) Write(bp BatchPoints) (*Response, error) {
	u := *c.url
	u.Path = "/write"

	values := url.Values{}
	values.Add("db", bp.Database)
	if bp.RetentionPolicy != "" {
		values.Add("rp", bp.RetentionPolicy)
	}
	values.Add("precision", c.precision)
	u.RawQuery = values.Encode()

	points := make([]string, len(bp.Points))
	for i, p := range bp.Points {
		tags := ""
		for k, v := range p.Tags {
			tags += fmt.Sprintf(",%s=%s", k, v)
		}
		fields := ""
		for k, v := range p.Fields {
			switch v := v.(type) {
			case float64:
				fields += fmt.Sprintf(",%s=%f", k, v)
			case int, int8, int16, int32, int64, uint, uint8, uint16, uint32, uint64:
				fields += fmt.Sprintf(",%s=%di", k, v)
			case bool:
				fields += fmt.Sprintf(",%s=%t", k, v)
			case string:
				fields += fmt.Sprintf(",%s=\"%s\"", k, v)
			default:
				return nil, fmt.Errorf("unsupported field type: %T", v)
			}
		}
		timeStr := ""
		if !p.Time.IsZero() {
			timeStr = p.Time.Format(time.RFC3339Nano)
		}
		points[i] = fmt.Sprintf("%s%s%s %s", p.Measurement, tags, fields, timeStr)
	}

	body := strings.Join(points, "\n")

	req, err := http.NewRequest("POST", u.String(), strings.NewReader(body))
	if err != nil {
		return nil, err
	}

	req.Header.Set("Content-Type", "text/plain")
	req.Header.Set("User-Agent", c.userAgent)
	if c.username != "" && c.password != "" {
		req.SetBasicAuth(c.username, c.password)
	}

	resp, err := c.httpClient.Do(req)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode < 200 || resp.StatusCode >= 300 {
		body, _ := ioutil.ReadAll(resp.Body)
		return nil, fmt.Errorf("influxdb: %s", strings.TrimSpace(string(body)))
	}

	return &Response{}, nil
}

