	"golang.org/x/net/ipv4"
)

func main() {
	listener, err := net.Listen("tcp", "0.0.0.0:1024")
	if err != nil {
		fmt.Println("Error starting server:", err)
		return
	}
	defer listener.Close()

	for {
		conn, err := listener.Accept()
		if err != nil {
			fmt.Println("Error accepting connection:", err)
			continue
		}

		go handleConnection(conn)
	}
}

func handleConnection(conn net.Conn) {
	defer conn.Close()

	tcpConn, ok := conn.(*net.TCPConn)
	if !ok {
		fmt.Println("Not a TCP connection")
		return
	}

	file, err := tcpConn.File()
	if err != nil {
		fmt.Println("Error getting file descriptor:", err)
		return
	}
	defer file.Close()

	ipConn := ipv4.NewConn(file)
	if err := ipConn.SetTOS(0x38); err != nil { // Set TOS to 0x38 (DSCP AF41)
		fmt.Println("Error setting TOS:", err)
		return
	}

	if err := ipConn.SetTTL(64); err != nil { // Set TTL to 64
		fmt.Println("Error setting TTL:", err)
		return
	}

	_, err = conn.Write([]byte("HELLO-R-U-THERE-ACK"))
	if err != nil {
		fmt.Println("Error sending message:", err)
	}
