func (s *Server) ListenAndServe() error {
	s.init()
	var err error
	if s.TLSConfig != nil {
		s.Listener, err = tls.Listen(s.Net, s.Addr, s.TLSConfig)
	} else {
		s.Listener, err = net.Listen(s.Net, s.Addr)
	}
	if err != nil {
		return err
	}
	defer s.Listener.Close()

	s.lock.Lock()
	s.started = true
	s.lock.Unlock()

	var wg sync.WaitGroup
	for {
		conn, err := s.Listener.Accept()
		if err != nil {
			select {
			case <-s.shutdown:
				return nil
			default:
				return err
			}
		}
		wg.Add(1)
		go func() {
			defer wg.Done()
			s.serveTCPConn(&wg, conn)
		}()
	}
}

