	s.lock.Lock()
	s.conns[conn] = struct{}{}
	s.lock.Unlock()
	defer func() {
		s.lock.Lock()
		delete(s.conns, conn)
		s.lock.Unlock()
	}()

	conn.SetDeadline(time.Now().Add(s.getReadTimeout()))
	buffer := make([]byte, 1024)
	for {
		n, err := conn.Read(buffer)
		if err != nil {
			return
		}
		msg := buffer[:n]
		if s.MsgAcceptFunc != nil && !s.MsgAcceptFunc(msg) {
			continue
		}
		conn.SetDeadline(time.Now().Add(s.WriteTimeout))
		if err := s.Handler.HandleMessage(conn, msg); err != nil {
			return
		}
		conn.SetDeadline(time.Now().Add(s.getReadTimeout()))
	}
}

