package main

import (
	"context"
	"flag"
	"time"

	"google.golang.org/grpc"
	pb "path/to/your/profiling/protobuf"
)

var (
	flagAddress        = flag.String("address", "localhost:50051", "gRPC server address")
	flagTimeout        = flag.Int("timeout", 10, "connection timeout in seconds (0 for no timeout)")
	flagEnableProfiling = flag.Bool("enable", false, "enable profiling")
	flagDisableProfiling = flag.Bool("disable", false, "disable profiling")
	flagRetrieveSnapshot = flag.Bool("snapshot", false, "retrieve a snapshot")
	flagSnapshot         = flag.String("id", "", "snapshot identifier")
)

func remoteCommand() error {
	var opts []grpc.DialOption
	opts = append(opts, grpc.WithInsecure())

	connCtx := context.Background()
	if *flagTimeout > 0 {
		var cancel context.CancelFunc
		connCtx, cancel = context.WithTimeout(connCtx, time.Duration(*flagTimeout)*time.Second)
		defer cancel()
	}

	conn, err := grpc.DialContext(connCtx, *flagAddress, opts...)
	if err != nil {
		return err
	}
	defer conn.Close()

	client := pb.NewProfilingServiceClient(conn)

	if *flagEnableProfiling {
		_, err = client.EnableProfiling(context.Background(), &pb.Empty{})
		if err != nil {
			return err
		}
	}

	if *flagDisableProfiling {
		_, err = client.DisableProfiling(context.Background(), &pb.Empty{})
		if err != nil {
			return err
		}
	}

	if *flagRetrieveSnapshot {
		_, err = client.RetrieveSnapshot(context.Background(), &pb.SnapshotRequest{Id: *flagSnapshot})
		if err != nil {
			return err
		}
	}

	return nil
}

