package main

import (
	"context"
	"fmt"
	"io"
	"log"
	"os"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials/insecure"
	"google.golang.org/grpc/metadata"

	commonv1 "github.com/dragonflyoss/Dragonfly/v2/api/proto/common/v1"
	dfdaemonv1 "github.com/dragonflyoss/Dragonfly/v2/api/proto/dfdaemon/v1"
)

func downloadFile(ctx context.Context, unixSocketPath string, downloadRequest *dfdaemonv1.DownRequest) (map[string]*dfdaemonv1.DownResult, error) {
	conn, err := grpc.DialContext(ctx, "unix://"+unixSocketPath, grpc.WithTransportCredentials(insecure.NewCredentials()))
	if err != nil {
		return nil, fmt.Errorf("did not connect: %w", err)
	}
	defer conn.Close()

	client := dfdaemonv1.NewDfdaemonClient(conn)

	md := metadata.New(downloadRequest.UrlMeta.Header)
	ctx = metadata.NewOutgoingContext(ctx, md)

	stream, err := client.Download(ctx, downloadRequest)
	if err != nil {
		return nil, fmt.Errorf("could not start download: %w", err)
	}

	results := make(map[string]*dfdaemonv1.DownResult)
	for {
		resp, err := stream.Recv()
		if err == io.EOF {
			break
		}
		if err != nil {
			return nil, fmt.Errorf("failed to receive response: %w", err)
		}
		results[resp.Output] = resp
	}

	return results, nil
}

