)

var (
	tls                = flag.Bool("tls", false, "Connection uses TLS if true, else plain TCP")
	serverHostOverride = flag.String("server_host_override", "x.test.youtube.com", "The server name used to verify the hostname returned by the TLS handshake")
	caFile             = flag.String("ca_file", "", "The file containing the CA root cert file")
	serverAddr         = flag.String("server_addr", "localhost:10000", "The server address in the format of host:port")
)

func main() {
	flag.Parse()
	var opts []grpc.DialOption
	if *tls {
		if *caFile == "" {
			*caFile = "../data/ca.pem"
		}
		creds, err := credentials.NewClientTLSFromFile(*caFile, *serverHostOverride)
		if err != nil {
			log.Fatalf("Failed to create TLS credentials %v", err)
		}
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithInsecure())
	}

	conn, err := grpc.Dial(*serverAddr, opts...)
	if err != nil {
		log.Fatalf("did not connect: %v", err)
	}
	defer conn.Close()
	client := pb.NewRouteGuideClient(conn)

	printFeature(client, &pb.Point{Latitude: 408123456, Longitude: -743999178}) // New York
	printFeature(client, &pb.Point{Latitude: 0, Longitude: 0})                  // Invalid point

	rectangle := &pb.Rectangle{
		Lo: &pb.Point{Latitude: 400000000, Longitude: -750000000},
		Hi: &pb.Point{Latitude: 420000000, Longitude: -730000000},
	}
	printFeatures(client, rectangle)

	runRecordRoute(client)

	runRouteChat(client)
}

func printFeature(client pb.RouteGuideClient, point *pb.Point) {
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
	feature, err := client.GetFeature(ctx, point)
	if err != nil {
		log.Fatalf("%v.GetFeatures(_) = _, %v", client, err)
	}
	log.Printf("Found feature called %s at %v", feature.GetName(), feature.GetLocation())
}

func printFeatures(client pb.RouteGuideClient, rect *pb.Rectangle) {
	log.Printf("Looking for features within %v", rect)
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
	stream, err := client.ListFeatures(ctx, rect)
	if err != nil {
		log.Fatalf("%v.ListFeatures(_) = _, %v", client, err)
	}
	for {
		feature, err := stream.Recv()
		if err != nil {
			log.Fatalf("%v.ListFeatures(_) = _, %v", client, err)
		}
		log.Printf("Found feature called %s at %v", feature.GetName(), feature.GetLocation())
	}
}

func runRecordRoute(client pb.RouteGuideClient) {
	route := []*pb.Point{
		{Latitude: 408123456, Longitude: -743999178},
		{Latitude: 408241893, Longitude: -743803379},
		{Latitude: 408383429, Longitude: -743517371},
		{Latitude: 408407828, Longitude: -743499171},
		{Latitude: 408577763, Longitude: -743502713},
		{Latitude: 408664916, Longitude: -743478481},
	}

	log.Println("Traversing route:")
	routeStream, err := client.RecordRoute(context.Background())
	if err != nil {
		log.Fatalf("%v.RecordRoute(_) = _, %v", client, err)
	}
	for _, point := range route {
		log.Printf("Visiting point %v", point)
		if err := routeStream.Send(point); err != nil {
			log.Fatalf("%v.Send(%v) = %v", routeStream, point, err)
		}
		time.Sleep(100 * time.Millisecond)
	}
	reply, err := routeStream.CloseAndRecv()
	if err != nil {
		log.Fatalf("%v.CloseAndRecv() got error %v, want %v", routeStream, err, nil)
	}
	log.Printf("Route summary: %v", reply)
}

func runRouteChat(client pb.RouteGuideClient) {
	notes := []*pb.RouteNote{
		{Location: &pb.Point{Latitude: 408123456, Longitude: -743999178}, Message: "First message"},
		{Location: &pb.Point{Latitude: 408241893, Longitude: -743803379}, Message: "Second message"},
		{Location: &pb.Point{Latitude: 408383429, Longitude: -743517371}, Message: "Third message"},
		{Location: &pb.Point{Latitude: 408407828, Longitude: -743499171}, Message: "Fourth message"},
		{Location: &pb.Point{Latitude: 408577763, Longitude: -743502713}, Message: "Fifth message"},
		{Location: &pb.Point{Latitude: 408664916, Longitude: -743478481}, Message: "Sixth message"},
	}

	ctx, cancel := context.WithCancel(context.Background())
	defer cancel()

	stream, err := client.RouteChat(ctx)
	if err != nil {
		log.Fatalf("%v.RouteChat(_) = _, %v", client, err)
	}
	waitc := make(chan struct{})
	go func() {
		for {
			in, err := stream.Recv()
			if err != nil {
				log.Fatalf("Failed to receive a note : %v", err)
			}
			log.Printf("Got message %s at point(%d, %d)", in.Message, in.Location.Latitude, in.Location.Longitude)
		}
	}()

	for _, note := range notes {
		if err := stream.Send(note); err != nil {
			log.Fatalf("Failed to send a note: %v", err)
		}
	}
	stream.CloseSend()
	<-waitc
}

