func createGRPCClient(serverAddr string, useTLS bool, caFile string, serverHostOverride string) (pb.RouteGuideClient, error) {
	var opts []grpc.DialOption
	if useTLS {
		creds, err := loadTLSCredentials(caFile, serverHostOverride)
		if err != nil {
			return nil, fmt.Errorf("failed to create TLS credentials: %v", err)
		}
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithInsecure())
	}

	conn, err := grpc.Dial(serverAddr, opts...)
	if err != nil {
		return nil, fmt.Errorf("did not connect: %v", err)
	}

	client := pb.NewRouteGuideClient(conn)
	return client, nil
}

func loadTLSCredentials(caFile string, serverHostOverride string) (credentials.TransportCredentials, error) {
	pemServerCA, err := ioutil.ReadFile(caFile)
	if err != nil {
		return nil, err
	}

	certPool := x509.NewCertPool()
	if !certPool.AppendCertsFromPEM(pemServerCA) {
		return nil, fmt.Errorf("failed to add server CA's certificate")
	}

	config := &tls.Config{
		RootCAs:            certPool,
		ServerName:         serverHostOverride,
		InsecureSkipVerify: false,
	}

	return credentials.NewTLS(config), nil
}

func main() {
	client, err := createGRPCClient("localhost:50051", true, "path/to/ca.crt", "example.com")
