		return err
	}
	defer conn.Close()

	c.client = pb.NewCloudSlackServiceClient(conn)
	stream, err := c.client.BidirectionalStream(ctx)
	if err != nil {
		return err
	}
	defer func() {
		if err := stream.CloseSend(); err != nil {
			c.log.Printf("Failed to close send: %v", err)
		}
	}()

	connReq := &pb.ConnectionRequest{
		BotId:         c.botID,
		ClusterName:   c.clusterName,
		Metadata:      c.commGroupMetadata,
