		HeartbeatRateMs: c.cfg.HeartbeatRateMs,
	}
	if err := stream.Send(connReq); err != nil {
		return err
	}

	for {
		in, err := stream.Recv()
		if err != nil {
			return err
		}
		c.handleEvent(in)
	}
}

