	lis, err := net.Listen("tcp", url.String())
	if err != nil {
		log.Fatalf("failed to listen: %v", err)
	}

	opts := []grpc.ServerOption{}

	if url.TLSConfig != nil {
		tlsConfig := &tls.Config{}
		if url.TLSConfig.CertFile != "" && url.TLSConfig.KeyFile != "" {
			serverCert, err := tls.LoadX509KeyPair(url.TLSConfig.CertFile, url.TLSConfig.KeyFile)
			if err != nil {
				log.Fatalf("could not load server key pair: %s", err)
			}
			tlsConfig.Certificates = []tls.Certificate{serverCert}
		}
		if url.TLSConfig.CAFile != "" {
			certPool := x509.NewCertPool()
			caCert, err := ioutil.ReadFile(url.TLSConfig.CAFile)
			if err != nil {
				log.Fatalf("could not read ca certificate: %s", err)
			}
			if !certPool.AppendCertsFromPEM(caCert) {
				log.Fatalf("failed to append ca certs")
			}
			tlsConfig.ClientAuth = tls.RequireAndVerifyClientCert
			tlsConfig.ClientCAs = certPool
		}
		opts = append(opts, grpc.Creds(credentials.NewTLS(tlsConfig)))
	} else {
		log.Println("TLS is disabled")
	}

	opts = append(opts, grpc.MaxRecvMsgSize(s.bufferSize))
	opts = append(opts, grpc.MaxSendMsgSize(s.bufferSize))

	tracer := opentracing.GlobalTracer()
	if tracer != nil {
		opts = append(opts, grpc.UnaryInterceptor(otgrpc.OpenTracingServerInterceptor(tracer)))
		opts = append(opts, grpc.StreamInterceptor(otgrpc.OpenTracingStreamServerInterceptor(tracer)))
	}

	s.grpcServer = grpc.NewServer(opts...)

	// Register your services here
