	reflection.Register(s.grpcServer)

	log.Printf("starting gRPC server on %s", url.String())
	if err := s.grpcServer.Serve(lis); err != nil {
		log.Fatalf("failed to serve: %v", err)
	}
}

func (s *Server) Stop() {
	if s.grpcServer != nil {
		s.grpcServer.Stop()
		log.Println("gRPC server stopped immediately")
	}
}

