}

func (s *ServergRPC) Query(ctx context.Context, in *pb.DnsPacket) (*pb.DnsPacket, error) {
	span, ctx := opentracing.StartSpanFromContext(ctx, "Query")
	defer span.Finish()

	packet, err := s.Server.Query(ctx, in)
	if err != nil {
		ext.LogError(span, err)
		return nil, err
	}
	return packet, nil
}

func (s *ServergRPC) Watch(stream pb.DnsService_WatchServer) error {
	span := opentracing.SpanFromContext(stream.Context())
	if span == nil {
		span, _ = opentracing.StartSpanFromContext(stream.Context(), "Watch")
		defer span.Finish()
	}

	return s.watch.Watch(stream)
}

func (s *ServergRPC) Stop() error {
	s.grpcServer.GracefulStop()
	s.watch.Stop()
	return nil
}

func (s *ServergRPC) Shutdown() error {
	s.grpcServer.Stop()
	s.watch.Stop()
	return nil
}

func (s *ServergRPC) unaryInterceptor(ctx context.Context, req interface{}, info *grpc.UnaryServerInfo, handler grpc.UnaryHandler) (interface{}, error) {
	span := opentracing.SpanFromContext(ctx)
	if span == nil {
		span, ctx = opentracing.StartSpanFromContext(ctx, info.FullMethod)
		defer span.Finish()
		ext.Component.Set(span, "gRPC Unary Interceptor")
		ext.RPCMethod.Set(span, info.FullMethod)
		ext.PeerHostname.Set(span, s.listenAddr.String())
		md, ok := metadata.FromIncomingContext(ctx)
		if ok {
			for k, v := range md {
				span.SetTag(fmt.Sprintf("metadata.%s", k), v)
			}
		}
	}
	resp, err := handler(ctx, req)
	if err != nil {
		ext.LogError(span, err)
	}
	return resp, err
}

func (s *ServergRPC) streamInterceptor(srv interface{}, ss grpc.ServerStream, info *grpc.StreamServerInfo, handler grpc.StreamHandler) error {
	span := opentracing.SpanFromContext(ss.Context())
	if span == nil {
		span, _ = opentracing.StartSpanFromContext(ss.Context(), info.FullMethod)
		defer span.Finish()
		ext.Component.Set(span, "gRPC Stream Interceptor")
		ext.RPCMethod.Set(span, info.FullMethod)
		ext.PeerHostname.Set(span, s.listenAddr.String())
		md, ok := metadata.FromIncomingContext(ss.Context())
		if ok {
			for k, v := range md {
				span.SetTag(fmt.Sprintf("metadata.%s", k), v)
			}
		}
	}
	wrapped := &wrappedStream{ss, span}
	return handler(srv, wrapped)
}

type wrappedStream struct {
	grpc.ServerStream
	span opentracing.Span
}

func (w *wrappedStream) SendMsg(m interface{}) error {
	err := w.ServerStream.SendMsg(m)
	if err != nil {
		ext.LogError(w.span, err)
	}
	return err
}

func (w *wrappedStream) RecvMsg(m interface{}) error {
	err := w.ServerStream.RecvMsg(m)
	if err != nil {
		ext.LogError(w.span, err)
	}
	return err
}

func loadTLSConfig() (*tls.Config, error) {
	cert, err := tls.LoadX509KeyPair("server.crt", "server.key")
	if err != nil {
		return nil, err
	}
	caCert, err := ioutil.ReadFile("ca.crt")
	if err != nil {
		return nil, err
	}
	caCertPool := x509.NewCertPool()
	caCertPool.AppendCertsFromPEM(caCert)
	return &tls.Config{
		Certificates: []tls.Certificate{cert},
		ClientAuth:   tls.RequireAndVerifyClientCert,
		ClientCAs:    caCertPool,
	}, nil
}

