	"google.golang.org/grpc/codes"
	"google.golang.org/grpc/status"
)

type LogGRPC func(ctx context.Context, info *grpc.UnaryServerInfo, err error)

type CSICreds struct {
	Token string
}

type CSIDriverServers struct {
	Controller *ControllerServer
	Identity   *IdentityServer
	Node       *NodeServer
}

type CSIDriver struct {
	listener  net.Listener
	server    *grpc.Server
	servers   *CSIDriverServers
	wg        sync.WaitGroup
	running   bool
	lock      sync.Mutex
	creds     *CSICreds
	logGRPC   LogGRPC
}

func NewCSIDriver(servers *CSIDriverServers) *CSIDriver {
	return &CSIDriver{
		servers: servers,
	}
}

func (d *CSIDriver) Start(listener net.Listener, interceptor grpc.UnaryServerInterceptor) error {
	d.lock.Lock()
	defer d.lock.Unlock()

	if d.running {
		return status.Errorf(codes.AlreadyExists, "server is already running")
	}

	d.listener = listener
	opts := []grpc.ServerOption{}
	if interceptor != nil {
		opts = append(opts, grpc.UnaryInterceptor(interceptor))
	}
	d.server = grpc.NewServer(opts...)
	RegisterControllerServer(d.server, d.servers.Controller)
	RegisterIdentityServer(d.server, d.servers.Identity)
	RegisterNodeServer(d.server, d.servers.Node)

	d.running = true
	d.wg.Add(1)
	go d.goServe(make(chan bool))

	return nil
}

func (d *CSIDriver) goServe(stopChan chan<- bool) {
	defer d.wg.Done()
	err := d.server.Serve(d.listener)
	if err != nil && err != grpc.ErrServerStopped {
		panic(err)
	}
	close(stopChan)
}

func (d *CSIDriver) Stop() {
	d.lock.Lock()
	defer d.lock.Unlock()

	if !d.running {
		return
	}

	d.server.GracefulStop()
	d.running = false
	d.wg.Wait()
}

func (d *CSIDriver) callInterceptor(ctx context.Context, req interface{}, info *grpc.UnaryServerInfo, handler grpc.UnaryHandler) (interface{}, error) {
	md, ok := metadata.FromIncomingContext(ctx)
	if !ok {
		return nil, status.Errorf(codes.Unauthenticated, "missing metadata")
	}

	authHeader, ok := md["authorization"]
	if !ok || len(authHeader) == 0 {
		return nil, status.Errorf(codes.Unauthenticated, "missing authorization header")
	}

	token := authHeader[0]
	if token != d.creds.Token {
		return nil, status.Errorf(codes.Unauthenticated, "invalid token")
	}

	resp, err := handler(ctx, req)
	if d.logGRPC != nil {
		d.logGRPC(ctx, info, err)
	}
	return resp, err
}

