package main

import (
	"bytes"
	"encoding/base64"
	"fmt"
	"io/ioutil"
	"net/http"
	"net/url"
	"strings"

	"github.com/prometheus/client_golang/prometheus"
	"github.com/prometheus/client_golang/prometheus/push"
	"github.com/prometheus/common/expfmt"
)

func doPush(job string, grouping map[string]string, pushURL string, g prometheus.Gatherer, method string) error {
	if job == "" {
		return fmt.Errorf("job name is required")
	}

	for k, v := range grouping {
		if k == "" || v == "" {
			return fmt.Errorf("both key and value in grouping must be non-empty")
		}
	}

	u, err := url.Parse(pushURL)
	if err != nil {
		return err
	}

	q := u.Query()
	q.Set("job", job)
	for k, v := range grouping {
		q.Set(k, v)
	}
	u.RawQuery = q.Encode()

	mfs, err := g.Gather()
	if err != nil {
		return err
	}

	var buf bytes.Buffer
	enc := expfmt.NewEncoder(&buf, expfmt.FmtProtoDelim)
	for _, mf := range mfs {
		if err := enc.Encode(mf); err != nil {
			return err
		}
	}

	client := &http.Client{}
	req, err := http.NewRequest(method, u.String(), &buf)
	if err != nil {
		return err
	}

	req.Header.Set("Content-Type", string(expfmt.FmtProtoDelim))
	req.Header.Set("Authorization", "Basic "+base64.StdEncoding.EncodeToString([]byte("username:password")))

	resp, err := client.Do(req)
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	body, _ := ioutil.ReadAll(resp.Body)
	if resp.StatusCode != http.StatusOK {
		return fmt.Errorf("failed to push metrics: %s (%d)", strings.TrimSpace(string(body)), resp.StatusCode)
	}

	return nil
}

