func New(address string, logger *logrus.Entry, opts ...Option) *Server {
	res := &Server{
		address:      address,
		logger:       logger,
		opts:         &options{},
		server:       grpc.NewServer(),
		readyChannel: make(chan struct{}),
	}

	for _, opt := range opts {
		opt(res.opts)
	}

	if res.opts.traceClient == nil {
