func NewServerWithPort(port int, opts ...grpc.ServerOption) (*Server, error) {
	addr := fmt.Sprintf(":%d", port)
	l, err := net.Listen("tcp", addr)
	if err != nil {
		return nil, err
	}
	s := &Server{
		Addr: addr,
		Port: port,
		l:    l,
		Gsrv: grpc.NewServer(opts...),
	}
	return s, nil
}

func (s *Server) Start() {
	s.mu.Lock()
	defer s.mu.Unlock()
	if !s.started {
		go func() {
			if err := s.Gsrv.Serve(s.l); err != nil {
				fmt.Println("Error serving:", err)
			}
		}()
		s.started = true
	}
