	s.mu.Lock()
	defer s.mu.Unlock()
	if s.Gsrv != nil {
		s.Gsrv.GracefulStop()
		s.Gsrv = nil
	}
	if s.l != nil {
		s.l.Close()
		s.l = nil
	}
	s.started = false
