	var listener net.Listener
	var err error

	if len(config.Listener) == 0 {
		return nil, grpc.Errorf("listener address not provided")
	}

	switch {
	case len(config.Listener) > 7 && config.Listener[:7] == "unix://":
		listener, err = net.Listen("unix", config.Listener[7:])
	case len(config.Listener) > 6 && config.Listener[:6] == "tcp://":
		listener, err = net.Listen("tcp", config.Listener[6:])
	default:
		return nil, grpc.Errorf("unsupported protocol")
	}

	if err != nil {
		return nil, grpc.Errorf("failed to listen: %v", err)
