	"go.opentelemetry.io/otel/trace"
	"log"
	"runtime/debug"
)

type PushConsumerConfig struct {
	NameServer []string
	GroupID    string
}

type PushConsumer struct {
	name            string
	PushConsumer    rocketmq.PushConsumer
	PushConsumerConfig
	subscribers     map[string]func(context.Context, ...*primitive.MessageExt) (consumer.ConsumeResult, error)
	interceptors    []primitive.Interceptor
	bucket          *ratelimit.Bucket
	started         bool
}

func (pc *PushConsumer) RegisterSingleMessage(f func(context.Context, *primitive.MessageExt) error) *PushConsumer {
	pc.subscribers["default"] = func(ctx context.Context, msgs ...*primitive.MessageExt) (consumer.ConsumeResult, error) {
		for _, msg := range msgs {
			if !pc.bucket.TakeAvailable(1) {
				log.Println("Rate limit exceeded")
				return consumer.ConsumeSuccess, nil
			}
			ctx, span := otel.Tracer(pc.name).Start(ctx, "ProcessMessage")
			defer span.End()
			defer func() {
				if r := recover(); r != nil {
					span.RecordError(fmt.Errorf("%v", r))
					span.SetStatus(trace.StatusError, fmt.Sprintf("%v", r))
					log.Printf("Recovered from panic: %v\nStack trace:\n%s", r, debug.Stack())
				}
			}()
			err := f(ctx, msg)
			if err != nil {
				span.RecordError(err)
				span.SetStatus(trace.StatusError, err.Error())
				return consumer.ConsumeRetryLater, nil
			}
		}
		return consumer.ConsumeSuccess, nil
	}
	return pc
}

func (pc *PushConsumer) Start() error {
	var err error
	pc.PushConsumer, err = rocketmq.NewPushConsumer(
		consumer.WithNsResolver(primitive.NewPassthroughResolver(pc.NameServer)),
		consumer.WithGroupName(pc.GroupID),
		consumer.WithConsumeFromWhere(consumer.ConsumeFromFirstOffset),
		consumer.WithInstanceName(pc.name),
		consumer.WithInterceptor(pc.interceptors...),
	)
	if err != nil {
		return err
	}

	for topic, handler := range pc.subscribers {
		err = pc.PushConsumer.Subscribe(topic, consumer.MessageSelector{}, handler)
		if err != nil {
			return err
		}
	}

	err = pc.PushConsumer.Start()
	if err != nil {
		return err
	}
	pc.started = true
	return nil
}

