var (
	fileCache      = make(map[string][]byte)
	fileCacheMutex sync.Mutex
	fileCacheEnable = true
	verboseReader   = false
)

func fetchFile(fileurl string) ([]byte, error) {
	if fileCacheEnable {
		fileCacheMutex.Lock()
		if content, found := fileCache[fileurl]; found {
			fileCacheMutex.Unlock()
			return content, nil
		}
		fileCacheMutex.Unlock()
	}

	resp, err := http.Get(fileurl)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, fmt.Errorf("failed to fetch file: %s", resp.Status)
	}

	content, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	if fileCacheEnable {
		fileCacheMutex.Lock()
		fileCache[fileurl] = content
		fileCacheMutex.Unlock()
	}

	if verboseReader {
		fmt.Printf("Fetched and cached file from %s\n", fileurl)
	}

	return content, nil
}

