	Files  map[string]string `json:"files"`
}

func googlefont(f string) []string {
	url := fmt.Sprintf("https://www.googleapis.com/webfonts/v1/webfonts?key=YOUR_API_KEY&sort=popularity&filter=family:%s", f)
	resp, err := http.Get(url)
	if err != nil || resp.StatusCode != 200 {
		return []string{}
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return []string{}
	}

	var data struct {
		Items []GoogleFont `json:"items"`
	}
	err = json.Unmarshal(body, &data)
	if err != nil {
		return []string{}
	}

	if len(data.Items) == 0 {
		return []string{}
	}

	font := data.Items[0]
	variants := make([]string, 0, len(font.Files))
	for variant := range font.Files {
		variants = append(variants, variant)
	}

	return variants
}

