package main

import (
	"bytes"
	"encoding/gob"
	"errors"
	"fmt"
	"io/ioutil"
	"net/http"
	"os"
	"path/filepath"
	"time"
)

var cacheDir = "cache"

func init() {
	if err := os.MkdirAll(cacheDir, 0755); err != nil {
		panic(err)
	}
}

func fetchFile(fileurl string) ([]byte, error) {
	cachePath := filepath.Join(cacheDir, fmt.Sprintf("%x", fileurl))
	cacheInfoPath := filepath.Join(cacheDir, fmt.Sprintf("%x.info", fileurl))

	var cacheInfo struct {
		LastFetched time.Time
		Expires     time.Time
	}

	if _, err := os.Stat(cachePath); err == nil {
		if _, err := os.Stat(cacheInfoPath); err == nil {
			file, err := os.Open(cacheInfoPath)
			if err != nil {
				return nil, err
			}
			defer file.Close()
			dec := gob.NewDecoder(file)
			if err := dec.Decode(&cacheInfo); err != nil {
				return nil, err
			}
			if time.Now().Before(cacheInfo.Expires) {
				return ioutil.ReadFile(cachePath)
			}
		}
	}

	resp, err := http.Get(fileurl)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return nil, errors.New(fmt.Sprintf("HTTP request failed with status %d", resp.StatusCode))
	}

	bodyBytes, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, err
	}

	if err := ioutil.WriteFile(cachePath, bodyBytes, 0644); err != nil {
		return nil, err
	}

	cacheInfo.LastFetched = time.Now()
	cacheInfo.Expires = cacheInfo.LastFetched.Add(1 * time.Hour)

	cacheInfoFile, err := os.Create(cacheInfoPath)
	if err != nil {
		return nil, err
	}
	defer cacheInfoFile.Close()

	enc := gob.NewEncoder(cacheInfoFile)
	if err := enc.Encode(cacheInfo); err != nil {
		return nil, err
	}

	return bodyBytes, nil
}

