	"time"
)

type WorkWeiXin struct {
	CorpID     string
	AgentID    string
	CorpSecret string
	Token      string
	tokenExpiry time.Time
}

type tokenResponse struct {
	AccessToken string `json:"access_token"`
	ExpiresIn   int    `json:"expires_in"`
	ErrCode     int    `json:"errcode"`
	ErrMsg      string `json:"errmsg"`
}

type messageRequest struct {
	ToUser  string `json:"touser"`
	MsgType string `json:"msgtype"`
	AgentID string `json:"agentid"`
	Text    struct {
		Content string `json:"content"`
	} `json:"text"`
	Safe int `json:"safe"`
}

func (w *WorkWeiXin) getToken() (string, error) {
	if w.Token != "" && w.tokenExpiry.After(time.Now()) {
		return w.Token, nil
	}

	url := fmt.Sprintf("https://qyapi.weixin.qq.com/cgi-bin/gettoken?corpid=%s&corpsecret=%s", w.CorpID, w.CorpSecret)
	resp, err := http.Get(url)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	var tokenResp tokenResponse
	err = json.Unmarshal(body, &tokenResp)
	if err != nil {
		return "", err
	}

	if tokenResp.ErrCode != 0 {
		return "", errors.New(tokenResp.ErrMsg)
	}

	w.Token = tokenResp.AccessToken
	w.tokenExpiry = time.Now().Add(time.Duration(tokenResp.ExpiresIn-300) * time.Second) // 5 minutes buffer

	return w.Token, nil
}

func (w *WorkWeiXin) Send(receivers []string, title string, content []byte, others ...string) error {
	token, err := w.getToken()
	if err != nil {
		return err
	}

	receiver := strings.Join(receivers, "|")
	msg := fmt.Sprintf("# %s\n%s", title, string(content))
	msgReq := messageRequest{
		ToUser:  receiver,
		MsgType: "text",
		AgentID: w.AgentID,
		Text: struct {
			Content string `json:"content"`
		}{
			Content: msg,
		},
		Safe: 0,
	}

	jsonData, err := json.Marshal(msgReq)
	if err != nil {
		return err
	}

	url := fmt.Sprintf("https://qyapi.weixin.qq.com/cgi-bin/message/send?access_token=%s", token)
	resp, err := http.Post(url, "application/json", bytes.NewBuffer(jsonData))
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return err
	}

	var response map[string]interface{}
	err = json.Unmarshal(body, &response)
	if err != nil {
		return err
	}

	if response["errcode"].(float64) != 0 {
		return errors.New(response["errmsg"].(string))
	}

	return nil
}

