type AuthMessage struct {
	Type    string `json:"type"`
	URL     string `json:"url,omitempty"`
	Token   string `json:"token,omitempty"`
	Error   string `json:"error,omitempty"`
}

func ssoLogin(endpoint string) string {
	interrupt := make(chan os.Signal, 1)
	signal.Notify(interrupt, syscall.SIGINT, syscall.SIGTERM)

	u := "wss://" + endpoint
	c, _, err := websocket.DefaultDialer.Dial(u, http.Header{})
	if err != nil {
		log.Println("Error connecting:", err)
		return ""
	}
	defer c.Close()

	done := make(chan struct{})

	go func() {
		defer close(done)
		for {
			_, message, err := c.ReadMessage()
			if err != nil {
				log.Println("Error reading message:", err)
				return
			}

			var msg AuthMessage
			err = json.Unmarshal(message, &msg)
			if err != nil {
				log.Println("Error unmarshalling message:", err)
				continue
			}

			switch msg.Type {
			case "AUTH_URL":
				fmt.Println("Please open the following URL in your browser to authenticate:", msg.URL)
			case "JWT_TOKEN":
				fmt.Println("Authentication successful!")
				done <- struct{}{}
				return
			case "ERROR":
				log.Println("Authentication error:", msg.Error)
				return
			default:
				log.Println("Unknown message type:", msg.Type)
			}
		}
	}()

	select {
	case <-done:
		_, message, err := c.ReadMessage()
		if err != nil {
			log.Println("Error reading final message:", err)
			return ""
		}

		var msg AuthMessage
		err = json.Unmarshal(message, &msg)
		if err != nil || msg.Token == "" {
			log.Println("Error extracting token:", err)
			return ""
		}

		return msg.Token
	case <-interrupt:
		log.Println("Interrupt received, closing connection...")
		err := c.WriteMessage(websocket.CloseMessage, websocket.FormatCloseMessage(websocket.CloseNormalClosure, ""))
		if err != nil {
			log.Println("Error closing connection:", err)
		}
		select {
		case <-done:
		case <-time.After(time.Second):
		}
		return ""
	}
}

