			case vncnetwork.VncMsg_capture_data:
				p.chImage <- msg.GetData()
			case vncnetwork.VncMsg_clipboard_event:
				p.chClipboard <- msg.GetClipboard()
			default:
			}
		}
	}()
	go func() {
