	if err != nil {
		log.Println("Failed to set websocket upgrade:", err)
		return
	}
	defer conn.Close()

	guacTunnel := tunnel.NewInetGuacamoleTunnel(s.Host, s.Port)
	err = guacTunnel.Connect()
	if err != nil {
		log.Println("Failed to connect to Guacamole tunnel:", err)
