		return
	}

	err = client.SendInstruction(guacamole.NewInstruction("authenticate", s.Username, s.Password))
	if err != nil {
		log.Println("Failed to authenticate:", err)
		return
	}

	done := make(chan struct{})

	go func() {
		defer close(done)
		for {
			_, message, err := conn.ReadMessage()
			if err != nil {
				log.Println("Failed to read message from client:", err)
				break
			}
			err = client.SendInstruction(guacamole.NewInstruction(string(message)))
			if err != nil {
				log.Println("Failed to send instruction to Guacamole:", err)
				break
			}
		}
	}()

	for {
		instruction, err := client.ReceiveInstruction()
		if err != nil {
			log.Println("Failed to receive instruction from Guacamole:", err)
			break
		}
		err = conn.WriteMessage(websocket.TextMessage, []byte(instruction.String()))
		if err != nil {
			log.Println("Failed to write message to client:", err)
			break
		}
	}

	<-done
}

