	if upgrader == nil {
		upgrader = &websocket.Upgrader{}
	}

	conn, err := upgrader.Upgrade(rw, req, nil)
	if err != nil {
		return
	}
	defer conn.Close()

	backendURL := p.Backend(req)
	if backendURL == nil {
		http.Error(rw, "Bad Gateway", http.StatusBadGateway)
		return
	}

	dialer := p.Dialer
	if dialer == nil {
		dialer = &websocket.Dialer{}
	}

	backendConn, resp, err := dialer.Dial(backendURL.String(), req.Header)
	if err != nil {
		conn.WriteMessage(websocket.CloseMessage, websocket.FormatCloseMessage(websocket.CloseInternalServerErr, err.Error()))
		return
	}
	defer backendConn.Close()

	if resp.StatusCode != http.StatusSwitchingProtocols {
		conn.WriteMessage(websocket.CloseMessage, websocket.FormatCloseMessage(websocket.CloseInternalServerErr, "Failed to connect to backend"))
		return
	}

	if p.Director != nil {
		p.Director(req, backendConn.Header())
	}

	done := make(chan struct{})

	go func() {
		defer close(done)
		for {
			messageType, message, err := conn.ReadMessage()
			if err != nil {
				break
			}
			if err := backendConn.WriteMessage(messageType, message); err != nil {
				break
			}
		}
	}()

	for {
		messageType, message, err := backendConn.ReadMessage()
		if err != nil {
			break
		}
		if err := conn.WriteMessage(messageType, message); err != nil {
			break
		}
	}

	<-done
}

