	local, err := upgrader.Upgrade(w, r, nil)
	if err != nil {
		return
	}
	defer local.Close()

	ctx, cancel := context.WithCancel(context.Background())
	defer cancel()

	chRemote := make(chan *network.Msg)
	go v.remoteRead(ctx, chRemote, local)
	go v.localRead(ctx, local, conn)

	select {
	case <-ctx.Done():
	}
}

func (v *VNC) remoteRead(ctx context.Context, ch <-chan *network.Msg, local *websocket.Conn) {
	for {
		select {
		case msg := <-ch:
			if msg.Type == network.MsgTypeImage {
				local.WriteMessage(websocket.BinaryMessage, msg.Data)
			} else if msg.Type == network.MsgTypeClipboard {
				v.chClipboard <- string(msg.Data)
			}
		case <-ctx.Done():
			return
		}
	}
}

func (v *VNC) localRead(ctx context.Context, local *websocket.Conn, remote *conn.Conn) {
	for {
		mt, message, err := local.ReadMessage()
		if err != nil {
			return
		}

		switch mt {
		case websocket.TextMessage:
			v.processTextMessage(remote, message)
		case websocket.BinaryMessage:
			v.processBinaryMessage(remote, message)
		}
	}
}

