package dnsresolver

import (
	"context"
	"encoding/binary"
	"errors"
	"net"
	"sync"
	"time"

	"go.uber.org/atomic"
	"go.uber.org/zap"
	"golang.org/x/net/dns/dnsmessage"
	"golang.org/x/net/netip"
)

type Bootstrap struct {
	fqdn        string
	port        uint16
	bootstrap   *net.UDPAddr
	qt          uint16
	logger      *zap.Logger
	updating    atomic.Bool
	nextUpdate  time.Time
	readyNotify chan struct{}
	m           sync.Mutex
	ready       bool
	addrStr     string
}

func (b *Bootstrap) resolve(ctx context.Context, qt uint16) (netip.Addr, uint32, error) {
	b.m.Lock()
	if b.ready {
		b.m.Unlock()
		ip, err := netip.ParseAddr(b.addrStr)
		return ip, 0, err
	}
	b.m.Unlock()

	conn, err := net.DialUDP("udp", nil, b.bootstrap)
	if err != nil {
		return netip.Addr{}, 0, err
	}
	defer conn.Close()

	var msg dnsmessage.Message
	msg.Header.ID = dnsmessage.ID(binary.BigEndian.Uint16([]byte{1, 2}))
	msg.Header.RecursionDesired = true
	qname, err := dnsmessage.NewName(b.fqdn)
	if err != nil {
		return netip.Addr{}, 0, err
	}
	msg.Questions = []dnsmessage.Question{
		{Name: qname, Type: dnsmessage.Type(qt), Class: dnsmessage.ClassINET},
	}

	var buf [512]byte
	err = msg.SetQuestion(msg.Questions[0])
	if err != nil {
		return netip.Addr{}, 0, err
	}
	_, err = conn.Write(buf[:msg.Len()])
	if err != nil {
		return netip.Addr{}, 0, err
	}

	conn.SetReadDeadline(time.Now().Add(5 * time.Second))
	n, err := conn.Read(buf[:])
	if err != nil {
		return netip.Addr{}, 0, err
	}

	var response dnsmessage.Message
	err = response.Unpack(buf[:n])
	if err != nil {
		return netip.Addr{}, 0, err
	}

	for _, rr := range response.Answers {
		switch r := rr.Body.(type) {
		case *dnsmessage.AResource:
			ip, _ := netip.AddrFromSlice(r.A[:])
			b.m.Lock()
			b.addrStr = ip.String()
			b.ready = true
			close(b.readyNotify)
			b.m.Unlock()
			return ip, rr.Header.TTL, nil
		case *dnsmessage.AAAAResource:
			ip, _ := netip.AddrFromSlice(r.AAAA[:])
			b.m.Lock()
			b.addrStr = ip.String()
			b.ready = true
			close(b.readyNotify)
			b.m.Unlock()
			return ip, rr.Header.TTL, nil
		}
	}

	return netip.Addr{}, 0, errors.New("no valid answer found")
}

