			return resp[:n], nil
		}

		if errors.Is(err, net.ErrTimeout) {
			time.Sleep(backoff)
			backoff *= 2
			if backoff > maxBackoff {
				backoff = maxBackoff
			}
		} else {
			return nil, err
		}
	}

	return nil, errors.New("communication failed after retries")
}

