	msg := []byte{0, 0, 0, 0, 0, 1234, 0, 80, 0, 0, 0, 3600}
	resp, err := n.call(msg, 2*time.Second)
	if err != nil {
		panic(err)
	}
	resultCode := binary.BigEndian.Uint16(resp[2:4])
	if resultCode != 0 {
		panic("NAT-PMP error: " + string(resp[4:]))
	}
